                // elementHighlight        : true,
                // snippetOpen             : false,
                // toolStyle               : '',
                // snippetsSidebarDisplay  : 'auto',
                // clearPreferences        : true,
            });

            /**
             * Submit Form Constructor
             */
            var btnGetHtml = document.querySelector('.btnSaveConstructor');
            btnGetHtml.addEventListener('click', function(e)
            {

                if( !builder.builderStuff ) {
                    alert('Builder is destroyed. Please click the Init button');
                    return;
                }

                var html = builder.html(); // get HTML

                //console.log(html);

                //$('.form-control-content').html(html);

                $('.formConstructor').append($('<textarea name="content" style="display:none"></textarea').html(html)).trigger('submit');

                e.preventDefault();

            });

        });
    </script>

    <script>
        if (document.querySelector('.three-cards-swiper')) {
            const swiper = new Swiper('.three-cards-swiper', {
                slidesPerView: 3,
                loop: false,
                spaceBetween: 20,
                breakpoints: {
                    0: {
                        slidesPerView: 1,
                        spaceBetween: 20
                    },
                    480: {
                        slidesPerView: 2,
                        spaceBetween: 20
                    },
                    768: {
                        slidesPerView: 3,
                        spaceBetween: 20
                    }
                }
            });
        }
        setTimeout(function() {
            let observer = new MutationObserver((mutations) => {
                mutations.forEach((mutation) => {
                    let oldValue = mutation.oldValue;
                    let newValue = mutation.target.textContent;
                    if (oldValue !== newValue) {
                        console.log('dom changed');
                        // if ( document.querySelector('.three-cards-swiper') ) {
                        //     console.log('inserted');
                        const swiper = new Swiper('.three-cards-swiper', {
                            slidesPerView: 3,
                            loop: false,
                            spaceBetween: 20,
                        });
                        // }
                    }
                });
            });

            observer.observe(document.querySelector('.is-builder'), {
                characterDataOldValue: true,
                subtree: false,
                childList: true,
                characterData: true
            });

        }, 2000);

    </script>
